import React from "react";

const Weather = () =>
class Weather extends React.Component {
    render() {
        return (
            <>
                {this.props.icon && <img src={"http://openweathermap.org/img/w/" + this.props.icon + ".png"} alt="weather icon"></img>}
                {this.props.city && this.props.country && <h2>Location: { this.props.city }, { this.props.country }</h2>}
                {this.props.desc && <p>Current Weather Conditions: { this.props.desc }</p>}
                {this.props.temp && <h3>Current Temperature: { this.props.temp } F</h3>}        
                {this.props.humidity && <h5>Current Humidity: { this.props.humidity } %</h5>}
                {this.props.error && <p>{this.props.error}</p>}              
            </>

        )
    }
};

export default Weather;
